float intensity = 1.5;
float flowSpeed = 2.0;
float glowWidth = 0.15;
float noiseScale = 8.0;

float3 coreColor = float3(0.9, 0.95, 1.0);
float3 midColor = float3(0.0, 0.7, 1.0);
float3 edgeColor = float3(0.0, 0.3, 0.8);

float hash(float2 p) {
    return frac(sin(dot(p, float2(12.9898, 78.233))) * 43758.5453);
}

float noise(float2 p) {
    float2 i = floor(p);
    float2 f = frac(p);
    f = f * f * (3.0 - 2.0 * f);
    float a = hash(i);
    float b = hash(i + float2(1.0, 0.0));
    float c = hash(i + float2(0.0, 1.0));
    float d = hash(i + float2(1.0, 1.0));
    return lerp(lerp(a, b, f.x), lerp(c, d, f.x), f.y);
}

float fbm(float2 p) {
    float value = 0.0;
    float amp = 0.5;
    for (int i = 0; i < 5; i++) {
        value += amp * noise(p);
        p *= 2.0;
        amp *= 0.5;
    }
    return value;
}

float4 MainPS(float2 texCoord : TEXCOORD0) : COLOR0 {
    float2 uv = texCoord;

    float centerDist = abs(uv.y - 0.5) * 2.0;

    float2 flowUV = uv;
    flowUV.x += iTime * flowSpeed * 0.1;
    flowUV.y += sin(uv.x * 10.0 + iTime * 2.0) * 0.02;

    float energy = fbm(flowUV * noiseScale);
    energy = pow(energy, 2.0) * intensity;

    float edgeGlow = 1.0 - smoothstep(0.0, glowWidth, centerDist);
    float coreGlow = 1.0 - smoothstep(0.0, glowWidth * 0.3, centerDist);

    float3 color = lerp(edgeColor, midColor, edgeGlow);
    color = lerp(color, coreColor, coreGlow);
    color += midColor * energy * edgeGlow;

    float scanline = sin(uv.x * 50.0 - iTime * 10.0) * 0.5 + 0.5;
    scanline = pow(scanline, 20.0) * 0.3 * edgeGlow;
    color += coreColor * scanline;

    float pulse = sin(uv.x * 20.0 - iTime * 8.0) * 0.5 + 0.5;
    pulse = pow(pulse, 5.0) * 0.4;
    color += midColor * pulse * edgeGlow;

    float alpha = (1.0 - centerDist) * intensity;
    alpha = saturate(alpha);
    alpha *= 0.8 + energy * 0.4;

    return float4(color * alpha, alpha);
}
