float4 MainPS(float2 texCoord : TEXCOORD0) : COLOR0
{
    float2 uv = texCoord;
    float2 p = uv * 2.0 - 1.0;
    float noise = frac(sin(dot(uv * 128.0, float2(12.9898, 78.233))) * 43758.5453);
    float vignette = saturate(1.0 - dot(p, p) * 0.55);
    float wave = 0.5 + 0.5 * sin(iTime * 1.2 + uv.x * 8.0);
    float3 col = float3(noise * 0.4 + wave * 0.6, uv.y, wave) * vignette;
    return float4(col, 1.0);
}
